def compute_hydrophobicity(sequence, scale):
    """Computes the average hydrophobicity of a sequence using a given scale."""
    return sum(scale.get(aa, 0) for aa in sequence) / len(sequence)

def process_fasta(fasta_file, output_json):
    """Reads a FASTA file, calculates hydrophobicity scores, and writes to JSON."""
    results = []
    
    for record in SeqIO.parse(fasta_file, "fasta"):
        seq_data = {
            "name": record.id,
            "sequence": str(record.seq),
            "hydrophobicity_norm_length": {scale: compute_hydrophobicity(str(record.seq), values) for scale, values in hydrophobicity_scales.items()}
        }
        results.append(seq_data)
    
    with open(output_json, "w") as json_file:
        json.dump(results, json_file, indent=4)

if __name__ == "__main__":
    if len(sys.argv) != 3:
        print("Incorrect input\nUsage: python script.py input.fasta output.json")
        sys.exit(1)
    
    input_fasta = sys.argv[1]
    output_json = sys.argv[2]
    
    process_fasta(input_fasta, output_json)
    print(f"Results saved to {output_json}")
